mapper.select:
SELECT u.we_chat_nick_name as 'name',SUM(m.diamonds) as 'number', max(DATE_FORMAT(m.create_date, '%Y-%m-%d %H:%i:%s')) as 'date' from tb_money m join tb_user u on m.user_id = u.user_id
where m.create_date BETWEEN #{startDate} and #{endDate}
GROUP BY m.user_id ORDER BY max(DATE_FORMAT(m.create_date, '%Y-%m-%d %H:%i:%s')) DESC
